</template>
<script>
import { defineComponent } from 'vue';
import Icon from '@ant-design/icons-vue';

const weather = '';
const temp = '';
let myCity = '';
const mineCity = '';

export default defineComponent({
  setup() {
    return {
    }
  },
  data() {
    return {
      weather,
      temp,
      myCity,
      mineCity,
    }
  },
  components: {
    Icon,
  },
  mounted() {
    this.getCity();
    this.getWeather();
    this.currentTime();
  },
  methods: {
    currentTime() {
      setInterval(this.getWeather, 100000);
    },
    getWeather() { // 第三方天气api接口
      console.log('getWeather()');
      const url = "https://v0.yiketianqi.com/api?unescape=1&version=v91&appid=43656176&appsecret=I42og6Lm&ext=&city=" + myCity;
      this.mineCity = myCity;
      console.log('url=' + url);
      console.log('myCity=' + myCity);
      console.log('mineCity=' + this.mineCity);
      this.axios
        .get(url)
        .then(res=> {
          this.weather = res.data.data[0].wea_day_img;
          this.temp = res.data.data[0].tem;
          console.log(this.weather);
        })
        .catch(function(error) {
          console.log('error: ' + error);
        });
    },
    getCity() {
      window.addEventListener('message', function(event) {
        city(event.data.city.slice(0, -1));
        console.log('getCity()');
      }, false);
    },
  },
});
function city(city) {
  myCity = city;
  console.log('city()');
  console.log('city=' + myCity);
}
</script>

